    $(document).ready(function () {
        $(".banner-about").css("background-image","url("+$(".1_largeIMG").val()+")")
        $(".search-box-container_main").load('/search_box.bc')
        //----------------JS OWL----------------//
        if($('#owl-demo_1').length){
            $("#owl-demo_1").owlCarousel({
                loop: false,
                autoplay: false,
                autoplayTimeout: 4000,
                items: 1,
                onInitialized: counter,
                onTranslated: counter
            });
        }
        if($('#owl-demo_2').length){
            $("#owl-demo_2").owlCarousel({
                loop: true,
                animateIn: 'fadeIn',
                animateOut: 'fadeOut',
                autoplay: true,
                smartSpeed: 450,
                items: 1,
            });
        }
        if($('#owl-demo_3').length){
            $("#owl-demo_3").owlCarousel({
                loop: true,
                autoplay: true,
                autoplayTimeout: 5000,
                items: 1,
            });
        }

        function counter(event) {
            var element = event.target; // DOM element, in this example .owl-carousel
            var items = event.item.count; // Number of items
            var item = event.item.index + 1; // Position of the current item

            // it loop is true then reset counter from 1
            if (item > items) {
                item = item - items
            }
            $('.counter').html(item + " / " + items)
            $(".banner-about").css("background-image","url("+$("."+item+"_largeIMG").val()+")")

        }
        //----------------JS AOS----------------//
        if($(".index-page").length){
            AOS.init();
        }
    });
    //----------------JS SCROLL HANDLER----------------//
    $(".scroll-handler-menu ul li").click(function () {
        $('html,body').animate({
            scrollTop: $("#" + $(this).attr("data-target") + "").offset().top -= 80
        }, 'slow');
    })
    //----------------JS SHOW NEXT ITEM----------------//
    $(".next-item").click(function () {
        $('html,body').animate({
            scrollTop: $("" + $(this).attr("href") + "").offset().top -= 80
        }, 'slow');
    })

    //----------------JS NOTIFICATION----------------//
    function notification_close(element) {
        $(".notification-container").hide()
    }
    //----------------JS QUESTION/ANSWER----------------//
    function show_answer(element) {
        $(element).closest("li").toggleClass('active')
        if ($(element).closest("li").find(".question-icon").hasClass('fa-plus')) {
            $(element).closest("li").find(".question-icon").removeClass("fa-plus")
            $(element).closest("li").find(".question-icon").addClass("fa-minus")
        } else {
            $(element).closest("li").find(".question-icon").addClass("fa-plus")
            $(element).closest("li").find(".question-icon").removeClass("fa-minus")
        }
        $(element).closest("li").find(".answer").toggleClass("unvisible")

    }
    //----------------JS SCROLL FOR HEADER/SCROLL HANDLER----------------//
    $(window).on("scroll", function (e) {
        var scroll = $(window).scrollTop();
        if ($(this).scrollTop() > 0) {
            $(".header").addClass("is-sticky")
            $(".sticky-flight-summary-info").addClass("opened")
            $(".sticky-flight-summary-info").removeClass("closed")
        } else {
            $(".header").removeClass("is-sticky")
            $(".sticky-flight-summary-info").removeClass("opened")
            $(".sticky-flight-summary-info").addClass("closed")
        }
        $(".content-container").each(function () {
            var line = $(this).find(".line")
            var data_content = $(this).attr("data-content")
            var hieghtThreshold = $(this).offset().top + 20;
            var hieghtThreshold_end = $(this).offset().top + $(this).height();
            if (scroll >= hieghtThreshold && scroll <= hieghtThreshold_end) {
                $(".scroll-handler-menu ul li").each(function () {
                    if ($(this).attr("data-target") == data_content) {
                        $(this).find(".line").addClass('line-active');
                    } else {
                        $(this).find(".line").removeClass('line-active');

                    }
                })

            }
        });
    });
    //<!---------------JS MENU RESPONSIVE--------------->//
